import React, { useState } from 'react';
import styled, { css } from 'styled-components';
import { Link } from 'react-router-dom';
import Person from './Person';
import Skills from './Skills';
import Portfolio from './Portfolio';

const BgArea = styled.div`
  background: #38d9a9;
  &:hover {
    background: #63e6be;
  }
  &:active {
    background: #20c997;
  }
  z-index: 5;
  display: flex;
  position: absolute;
  left: 50%;
  bottom: 0;
  width: 80px;
  height: 80px;
  align-items: center;
  justify-content: center;
  transform: translate(-50%, 50%);
  cursor: pointer;

  font-size: 60px;
  color: #fff;
  border-radius: 50%;
  border: none;
  outline: none;

  transition: 0.125s all ease-in;

  ${(props) =>
    props.open &&
    css`
      background: #ff6b6b;
      &:hover {
        background: #ff8787;
      }
      &:active {
        background: #fa5252;
      }
      transform: translate(-50%, 50%) rotate(45deg);
    `}
`;

export default function Home() {
  const [open, setOpen] = useState(false);
  window.onload = () => {
    console.log('시작');
    setOpen(true);
  };

  return (
    <>
      <bgArea className="bg_area" open={open}></bgArea>
      <section id="home" className="section main_section">
        <div className="obj_area">
          <span className="field">portfolio 2020</span>
          <h3 className="obj">
            choi
            <br />
            myeonghui
          </h3>
          <nav className="sub_nav">
            <ul className="btn_group">
              <li>
                <Link to="#person" className="btn link scrl_link">
                  PERSON
                </Link>
              </li>
              <li>
                <Link to="#skills" className="btn link scrl_link">
                  SKILLS
                </Link>
              </li>
              <li>
                <Link to="#portfolio" className="btn link scrl_link">
                  PORTFOLIO
                </Link>
              </li>
            </ul>
          </nav>
        </div>
        <figure className="figure_main">
          <img src="../assets/images/img_main_visual.svg" alt="main visual" />
        </figure>
      </section>
      <Person />
      <Skills />
      <Portfolio />
    </>
  );
}
